mapper.select:
/* com.binblur.mapper.code.selectCodeListPaging */ SELECT ( COUNT(1) OVER() ) AS totalRowCount , ROW_NUMBER() OVER(ORDER BY TC.ID ASC) AS no , TC.ID AS id , TC.CODE_UPPER AS codeUpper , COALESCE((SELECT X.CODE_NM FROM TB_CODE X WHERE X.CODE_CD = TC.CODE_UPPER LIMIT 1), TC.CODE_UPPER) AS codeUpperNm , TC.CODE_CD AS codeCd , TC.CODE_NM AS codeNm , (SELECT COUNT(*) FROM TB_CODE X WHERE X.CODE_UPPER = TC.CODE_CD) AS lowerCodeCnt , CASE WHEN COALESCE(TC.CODE_DESC, '') = '' THEN '-' ELSE TC.CODE_DESC END AS codeDesc , CASE WHEN COALESCE(TC.CODE_ETC_1, '') = '' THEN '-' ELSE TC.CODE_ETC_1 END AS codeEtc1 , CASE WHEN COALESCE(TC.CODE_ETC_2, '') = '' THEN '-' ELSE TC.CODE_ETC_2 END AS codeEtc2 , TC.CODE_ORDER AS codeOrder , TC.CRE_TM AS createDateTime , TC.CRE_USER AS createUser , TC.UPD_TM AS updateDateTime , TC.UPD_USER AS updateUser , TC.DEL_YN AS deleteYn FROM TB_CODE TC WHERE 1 = 1 -- AND TC.DEL_YN = 'N' // 삭제여부를 사용여부로 관리
AND TC.CODE_UPPER = #{filter.codeUpper}
AND TC.CODE_CD LIKE '%' + #{filter.codeCd} + '%'
AND TC.CODE_NM LIKE '%' + #{filter.codeNm} + '%'
ORDER BY TC.ID DESC OFFSET (#{curPage}-1) * #{pageRowCount} ROWS FETCH NEXT #{pageRowCount} ROWS ONLY

/* com.binblur.mapper.code.selectDropdownByCodeRequest */
SELECT 
FROM TB_CODE
WHERE CODE_UPPER = #{codeUpper}

  AND CODE_NM LIKE '%' + #{codeNm} + '%'

  AND DEL_YN = 'N'

/* com.binblur.mapper.code.selectCodeByUk */
SELECT 
FROM TB_CODE
WHERE CODE_UPPER = #{codeUpper}
  AND CODE_CD = #{codeCd}
  AND DEL_YN = 'N'

mapper.insert:
/* com.binblur.mapper.code.insertCode */
INSERT INTO TB_CODE
(
      CODE_UPPER
    , CODE_CD
    , CODE_NM

    , CODE_DESC


    , CODE_ETC_1

    , CODE_ORDER
    , CRE_USER
    , UPD_USER

    , DEL_YN

)
VALUES
(
      #{codeUpper}
    , #{codeCd}
    , #{codeNm}

    , #{codeDesc}


    , #{codeEtc1}

    , #{codeOrder}
    , #{createUser}
    , #{createUser}

    , #{deleteYn}

 )

mapper.update:
/* com.binblur.mapper.code.updateCode */
UPDATE TB_CODE
SET UPD_TM   = NOW()
  , UPD_USER = #{updateUser}

  , CODE_UPPER = #{codeUpper}


  , CODE_CD = #{codeCd}


  , CODE_NM = #{codeNm}


  , CODE_DESC = #{codeDesc}


  , CODE_ETC_1 = #{codeEtc1}


  , CODE_ORDER = #{codeOrder}


  , DEL_YN = #{deleteYn}

WHERE ID = #{id}

mapper.delete:
/* com.binblur.mapper.code.deleteCode */
DELETE FROM TB_CODE
WHERE ID = #{id}

mapper.select:
/* com.binblur.mapper.code.selectDropdownByCodeUpper */
SELECT

FROM TB_CODE TC
WHERE 1 = 1
AND TC.DEL_YN = 'N'
AND TC.CODE_UPPER = #{codeUpper}

    AND TC.CODE_NM LIKE '%' + #{codeNm} + '%'

ORDER BY ${sortColumn} ${sortType}

/* com.binblur.mapper.code.selectCodePaging */ SELECT
, TC.CODE_NM AS codeNm , ( COUNT(1) OVER() ) AS totalRowCount , ROW_NUMBER() OVER(ORDER BY TC.CODE_ORDER ASC) AS no , COALESCE((SELECT X.CODE_NM FROM TB_CODE X WHERE X.CODE_CD = TC.CODE_UPPER LIMIT 1), TC.CODE_UPPER) AS codeUpperNm , CASE WHEN #{filter.codeUpper} = 'TOP' THEN (SELECT COUNT(*) FROM TB_CODE X WHERE X.DEL_YN = 'N' AND X.CODE_UPPER = TC.CODE_CD) ELSE 0 END AS lowerCodeCnt , CASE WHEN TC.CODE_UPPER = 'S0001' THEN LPAD('*', CHAR_LENGTH(TC.CODE_ETC_1)) ELSE TC.CODE_ETC_1 END AS codeEtc , CASE WHEN TC.DEL_YN = 'Y' THEN 'N' ELSE 'Y' END AS delYn FROM TB_CODE TC WHERE 1 = 1 AND TC.CODE_UPPER = #{filter.codeUpper}
AND TC.CODE_CD LIKE CONCAT('%' ,#{filter.codeCd} ,'%')
AND TC.CODE_NM LIKE CONCAT('%' ,#{filter.codeNm} ,'%')
ORDER BY TC.CODE_ORDER ASC OFFSET ((#{curPage}-1) * #{pageRowCount}) ROWS FETCH NEXT #{pageRowCount} ROWS ONLY

/* com.binblur.mapper.code.selectCodeByUkIncludeDelete */
SELECT

FROM TB_CODE TC
WHERE 1 = 1
AND TC.CODE_UPPER = #{codeUpper}
AND TC.CODE_CD = #{codeCd}
