mapper.insert:
/* [그리드 컬럼 등록 및 수정] com.shop.mapper.common.gridColumn.upsertGridColumn */
INSERT INTO TB_GRID
(
  USER_ID
, URI
, SET_VALUE
)
VALUES
(
  #{userId}
, #{uri}
, #{setValue}::json
)
ON CONFLICT (USER_ID, URI)   /* USER_ID와 URI는 복합키 */
DO UPDATE
      SET SET_VALUE = EXCLUDED.SET_VALUE;

mapper.select:
/* [그리드 컬럼 등록 및 수정] com.shop.mapper.common.gridColumn.selectGridColum */
SELECT URI
     , SET_VALUE AS columnState
  FROM TB_GRID
 WHERE USER_ID = #{userId}
   AND URI = #{uri}

mapper.delete:
DELETE
FROM TB_GRID
WHERE USER_ID = #{userId}
  AND URI = #{uri}
