mapper.select:
/* [하위 발주건수 조회] com.binblur.mapper.batch.selectAsnCountByParentId */
SELECT COUNT(1)
  FROM TB_ASN
 WHERE PARENT_ID = #{parentId}

mapper.insert:
/* [입하잔량 발주생성] com.binblur.mapper.batch.insertAsnForBatch */ INSERT INTO TB_ASN (
${column}
, GEN_CNT , ASN_CNT , OUT_YMD , PARENT_ID , ASN_STAT_CD , ASN_ORIGIN , CRE_USER , CRE_TM , UPD_USER , UPD_TM ) SELECT
A.${column}
, A.GEN_CNT - S.STOCK_CNT , 0 , NULL , A.ID /* 참조아이디로 사용(parentId) */ , '2' /* 발주확정처리 */ , '잔량' , 'SYSTEM' , NOW() , 'SYSTEM' , NOW() FROM TB_ASN A JOIN TB_STOCK S ON A.ID = S.ASN_ID WHERE A.ID = #{asnId}

/* [배치로그생성] com.binblur.mapper.batch.insertBatchLog */
INSERT INTO TB_BATCH
(
  BATCH_TP
, START_TM
, END_TM
, TARGET_CNT
, TRAN_CNT
, FAIL_CNT
, RSLT_CD
, MESSAGE
)
VALUES
(
  #{batchTp}
, #{startTm}
, #{endTm}
, #{targetCnt}
, #{tranCnt}
, #{failCnt}
, #{rsltCd}
, #{message}
)
