mapper.select:
 /* com.shop.mapper.authToken.selectAuthTokenByUserId */
 SELECT

 FROM TB_API_AUTH_TOKEN
WHERE USER_ID = #{userId}
ORDER BY ID DESC
LIMIT 1

/* com.shop.mapper.authToken.selectAuthTokenByAccessToken */
SELECT

FROM TB_API_AUTH_TOKEN
WHERE ACC_TOKEN = #{accessToken}
ORDER BY ID DESC
LIMIT 1

mapper.insert:
/* com.shop.mapper.authToken.createAuthToken */
INSERT INTO TB_API_AUTH_TOKEN
(
      USER_ID
    , ACC_TOKEN
    , ACC_TOKEN_EXPIRE
    , REF_TOKEN
    , REF_TOKEN_EXPIRE
    , CRE_TM
    , CRE_USER
)
VALUES
(
      #{userId}
    , #{accessToken}
    , #{accessTokenExpireDateTime}
    , #{refreshToken}
    , #{refreshTokenExpireDateTime}
    , CURRENT_TIMESTAMP
    , 'SYSTEM'
)

mapper.update:
/* com.shop.mapper.authToken.updateTokenByLogout */
UPDATE TB_API_AUTH_TOKEN SET
    ACC_TOKEN_EXPIRE = CURRENT_TIMESTAMP
   , REF_TOKEN_EXPIRE = CURRENT_TIMESTAMP
WHERE USER_ID = #{userId}
  AND ACC_TOKEN = #{accessToken}
