mapper.select:
/* com.binblur.mapper.code.selectCodeListPaging */ SELECT ( COUNT(1) OVER() ) AS totalRowCount , ROW_NUMBER() OVER(ORDER BY TC.ID ASC) AS no , TC.ID AS id , TC.CODE_UPPER AS codeUpper , COALESCE((SELECT X.CODE_NM FROM TB_CODE X WHERE X.CODE_CD = TC.CODE_UPPER LIMIT 1), TC.CODE_UPPER) AS codeUpperNm , TC.CODE_CD AS codeCd , TC.CODE_NM AS codeNm , (SELECT COUNT(*) FROM TB_CODE X WHERE X.CODE_UPPER = TC.CODE_CD) AS lowerCodeCnt , CASE WHEN COALESCE(TC.CODE_DESC, '') = '' THEN '-' ELSE TC.CODE_DESC END AS codeDesc , CASE WHEN COALESCE(TC.CODE_ETC_1, '') = '' THEN '-' ELSE TC.CODE_ETC_1 END AS codeEtc1 , CASE WHEN COALESCE(TC.CODE_ETC_2, '') = '' THEN '-' ELSE TC.CODE_ETC_2 END AS codeEtc2 , TC.CODE_ORDER AS codeOrder , TC.CRE_TM AS creTm , TC.CRE_USER AS creUser , TC.UPD_TM AS updTm , TC.UPD_USER AS updUser , TC.DEL_YN AS deleteYn FROM TB_CODE TC WHERE 1 = 1 -- AND TC.DEL_YN = 'N' // 삭제여부를 사용여부로 관리
AND TC.CODE_UPPER = #{filter.codeUpper}
AND TC.CODE_CD LIKE '%' + #{filter.codeCd} + '%'
AND TC.CODE_NM LIKE '%' + #{filter.codeNm} + '%'
ORDER BY TC.ID DESC OFFSET (#{curPage}-1) * #{pageRowCount} ROWS FETCH NEXT #{pageRowCount} ROWS ONLY

/* com.binblur.mapper.code.selectDropdownByCodeRequest */
SELECT 
FROM TB_CODE
WHERE CODE_UPPER = #{codeUpper}

  AND CODE_NM LIKE '%' + #{codeNm} + '%'

  AND DEL_YN = 'N'

/* com.binblur.mapper.code.selectCodeByUk */
SELECT 
FROM TB_CODE
WHERE CODE_UPPER = #{codeUpper}
  AND CODE_CD = #{codeCd}
  AND DEL_YN = 'N'

mapper.insert:
/* com.binblur.mapper.code.insertCode */
INSERT INTO TB_CODE
(
      CODE_UPPER
    , CODE_CD
    , CODE_NM

    , CODE_DESC


    , CODE_ETC_1

    , CODE_ORDER
    , CRE_USER
    , UPD_USER

    , DEL_YN

)
VALUES
(
      #{codeUpper}
    , #{codeCd}
    , #{codeNm}

    , #{codeDesc}


    , #{codeEtc1}

    , #{codeOrder}
    , #{creUser}
    , #{creUser}

    , #{deleteYn}

 )

mapper.update:
/* com.binblur.mapper.code.updateCode */
UPDATE TB_CODE
SET UPD_TM   = NOW()
  , UPD_USER = #{updUser}

  , CODE_UPPER = #{codeUpper}


  , CODE_CD = #{codeCd}


  , CODE_NM = #{codeNm}


  , CODE_DESC = #{codeDesc}


    , CODE_ETC_1 = #{codeEtc1}


    , CODE_ETC_2 = #{codeEtc2}


  , CODE_ORDER = #{codeOrder}


  , DEL_YN = #{delYn}

WHERE ID = #{id}

mapper.delete:
/* com.binblur.mapper.code.deleteCode */
DELETE FROM TB_CODE
WHERE ID = #{id}

mapper.select:
/* com.binblur.mapper.code.selectDropdownByCodeUpper */ SELECT ID AS id , CODE_UPPER AS codeUpper , CODE_CD AS codeCd , CODE_NM AS codeNm , CODE_DESC AS codeDesc , CODE_ETC_1 AS codeEtc1 , CODE_ETC_2 AS codeEtc2 , CODE_ORDER AS codeOrder FROM TB_CODE TC WHERE TC.DEL_YN = 'N' AND TC.CODE_UPPER = #{codeUpper}
AND TC.CODE_NM LIKE '%' + #{codeNm} + '%'
AND TC.CODE_ETC_1 IN ('ALL', #{etc1Val})
ORDER BY CODE_CD ${sortType} /* ORDER BY ${sortColumn} ${sortType} */

/* com.binblur.mapper.code.selectCodeByUkIncludeDelete */
SELECT

FROM TB_CODE TC
WHERE 1 = 1
AND TC.CODE_UPPER = #{codeUpper}
AND TC.CODE_CD = #{codeCd}

/* com.binblur.mapper.code.getLiveProductCode */
SELECT  LPAD(COALESCE((
                    SELECT CODE_CD::INT
                    FROM TB_CODE
                    WHERE CODE_UPPER = '80004'
                      AND CODE_ETC_1 = #{sellerKey}
                      AND CODE_NM = #{prodNm}
                ), 0)::TEXT, 3, '0')

/* com.binblur.mapper.code.createLiveProductCode */
SELECT  LPAD(COALESCE((
                          SELECT MAX(CODE_CD::INT) + 1
                          FROM TB_CODE
                          WHERE CODE_UPPER = '80004'
                            AND CODE_ETC_1 = #{sellerKey}
                      ), 1)::TEXT, 3, '0')

/* com.binblur.mapper.code.selectCodeName */
SELECT CODE_NM
FROM TB_CODE
WHERE CODE_UPPER = #{codeUpper}
  AND CODE_CD = #{codeCd}
  AND DEL_YN = 'N'
