mapper.update:
/* com.binblur.mapper.company.updateCompany */
UPDATE TB_COMP
SET
UPD_TM   = CURRENT_TIMESTAMP,
UPD_USER = #{updUser}

    , COMP_NAME = #{compNm}


    , COMP_ADDR_DESC = #{compAddr}


    , COMP_CNTN = #{compCntn}

WHERE ID = #{id}

mapper.insert:
/* com.binblur.mapper.company.insertCompany */
INSERT INTO TB_COMP
(
      COMP_NAME
    , COMP_ADDR_DESC

    , COMP_CNTN

    , COMP_LEVEL_CODE
    , CRE_USER
    , CRE_TM

    , DEL_YN

) VALUES
(
      #{compNm}
    , #{compAddr}

    , #{compCntn}

    , #{compLevelCode}
    , #{creUser}
    , CURRENT_TIMESTAMP

    , #{deleteYn}

)

mapper.update:
/* com.binblur.mapper.company.deleteCompany */
UPDATE TB_COMP
SET UPD_TM   = NOW()
  , UPD_USER = #{updUser}
  , DEL_YN   = 'Y'
WHERE ID = #{id}
