mapper.select:
/* com.smart.mapper.menu.selectMenuByUK */
SELECT

FROM TB_MENU TM
WHERE 1=1
AND DEL_YN = 'N'
AND UP_MENU_CD = #{upMenuCd}
AND MENU_CD = #{menuCd}

    AND MENU_ORDER = #{menuOrder}


    AND MENU_NM = #{menuNm}


    AND MENU_ENG_NM = #{menuEngNm}


    AND MENU_URI = #{menuUri}

/* com.smart.mapper.menu.selectMenuListPaging */ SELECT
, COUNT(1) OVER() AS totalRowCount , ROW_NUMBER() OVER(ORDER BY MENU_ORDER ASC, TM.ID ASC) AS no , (SELECT X.MENU_NM FROM TB_MENU X WHERE X.MENU_CD = TM.UP_MENU_CD) AS menuUpperNm FROM TB_MENU TM WHERE TM.DEL_YN = 'N'
AND TM.MENU_CD = TM.UP_MENU_CD /* 탑메뉴 */
AND UP_MENU_CD != MENU_CD AND UP_MENU_CD = #{filter.upMenuCd}
ORDER BY MENU_ORDER ASC OFFSET ((#{curPage}-1) * #{pageRowCount}) ROWS FETCH NEXT #{pageRowCount} ROWS ONLY

mapper.insert:
/* com.smart.mapper.menu.insertMenu */
INSERT INTO TB_MENU
(
      UP_MENU_CD
    , MENU_CD
    , MENU_ORDER
    , MENU_NM
    , MENU_ENG_NM
    , MENU_URI
    , CRE_USER
    , UPD_USER
)
VALUES
(
      #{upMenuCd}
    , #{menuCd}
    , #{menuOrder}
    , #{menuNm}
    , #{menuEngNm}
    , #{menuUri}
    , #{createUser}
    , #{createUser}
 )

mapper.update:
/* com.smart.mapper.menu.updateMenu */
UPDATE TB_MENU
SET UPD_TM   = NOW()
  , UPD_USER = #{updateUser}

  , UP_MENU_CD = #{upMenuCd}


  , MENU_CD = #{menuCd}


  , MENU_ORDER = #{menuOrder}


    , MENU_NM = #{menuNm}


    , MENU_ENG_NM = #{menuEngNm}


    , MENU_URI = #{menuUri}

WHERE ID = #{id}

/* com.smart.mapper.menu.deleteMenu */
UPDATE TB_MENU
SET UPD_TM   = NOW()
  , UPD_USER = #{updateUser}
  , DEL_YN   = 'Y'
WHERE ID = #{id}

mapper.select:
/* com.smart.mapper.menu.selectMenuAuth */
SELECT

  FROM TB_MENU_AUTH
 WHERE AUTH_CD = #{authCd}
   AND MENU_CD = #{menuCd}

    AND MENU_READ_YN =#{menuReadYn}


    AND MENU_UPD_YN =#{menuUpdYn}


    AND MENU_EXCEL_YN =#{menuExcelYn}

/* com.smart.mapper.menu.selectMenuAuthByUk */
SELECT

FROM TB_MENU_AUTH
WHERE AUTH_CD = #{authCd}
AND MENU_CD = #{menuCd}

mapper.insert:
/* com.smart.mapper.menu.insertMenuAuth */
INSERT INTO TB_MENU_AUTH
(
      AUTH_CD
    , MENU_CD
    , MENU_READ_YN
    , MENU_UPD_YN
    , MENU_EXCEL_YN
    , CRE_USER
    , UPD_USER
)
VALUES
(
  #{authCd}
, #{menuCd}
, #{menuReadYn}
, #{menuUpdYn}
, #{menuExcelYn}
, #{createUser}
, #{createUser}
)

mapper.update:
/* com.smart.mapper.menu.updateMenu */
UPDATE TB_MENU_AUTH
SET UPD_TM   = NOW()
  , UPD_USER = #{updateUser}
  , MENU_READ_YN = #{menuReadYn}
  , MENU_UPD_YN = #{menuUpdYn}
  , MENU_EXCEL_YN = #{menuExcelYn}
WHERE AUTH_CD = #{authCd}
  AND MENU_CD = #{menuCd}

mapper.select:
/* com.kefico.mapper.menu.selectMenuByNm */
SELECT COUNT(*)
FROM TB_MENU TM
WHERE 1 = 1
  AND DEL_YN = 'N'
  AND MENU_NM = #{menuNm}
  AND ID != #{id}
